mapper.insert:
/* addTestcase - 퀴즈 테스트케이스 추가 */ INSERT INTO TESTCASE(TESTCASE_NO, QUIZ_NO, TESTCASE_INPUT, TESTCASE_OUTPUT, TESTCASE_TYPE) SELECT TESTCASE_SEQ.NEXTVAL as "TESTCASE_NO", t.* FROM (
SELECT #{item.quiz_No} QUIZ_NO, #{item.testcase_Input} TESTCASE_INPUT, #{item.testcase_Output} TESTCASE_OUTPUT, #{item.testcase_Type} TESTCASE_TYPE FROM DUAL
) t

/* addQuiz - 퀴즈 문제 추가 */
SELECT QUIZ_SEQ.NEXTVAL FROM DUAL
INSERT INTO QUIZ(QUIZ_NO, MEMBER_ID, QUIZ_TITLE, QUIZ_CONTENTS, QUIZ_TYPE, QUIZ_LEVEL, QUIZ_POINT, QUIZ_PRICE, QUIZ_SAMPLECODE) VALUES (#{quiz_No}, #{member_Id}, #{quiz_Title}, #{quiz_Contents}, #{quiz_Type}, #{quiz_Level}, #{quiz_Point}, #{quiz_Price}, #{quiz_SampleCode})

mapper.select:
/* getQuizTotalCount - 퀴즈 전체 갯수 조회 */
SELECT QUIZ_NO, QUIZ_TITLE, QUIZ_CONTENTS, QUIZ_SAMPLECODE, QUIZ_LEVEL, QUIZ_TYPE, QUIZ_POINT, QUIZ_PRICE
FROM QUIZ
WHERE QUIZ_NO = #{quiz_No}

/* getExampleTestCases - 퀴즈 테스트케이스 조회 */
SELECT TESTCASE_NO, TESTCASE_INPUT, TESTCASE_OUTPUT, TESTCASE_TYPE FROM TESTCASE
WHERE QUIZ_NO = #{dto.quiz_No}

    AND TESTCASE_TYPE = #{type}

/* getAnswer - 퀴즈 제출답안 조회 */
SELECT QUIZ_NO, MEMBER_ID, SOURCECODE, ANSWER_DATE, ANSWER_CHECK FROM MEMBER_QUIZ_ANSWER
WHERE MEMBER_ID = #{member_Id} AND QUIZ_NO = #{quiz_No}

mapper.update:
/* updateAnswer - 퀴즈 제출답안 수정 */
UPDATE MEMBER_QUIZ_ANSWER SET SOURCECODE = #{sourcecode}, ANSWER_DATE = SYSDATE, ANSWER_CHECK = #{answer_Check}
WHERE MEMBER_ID = #{member_Id} AND QUIZ_NO = #{quiz_No}

mapper.insert:
/* setAnswer - 퀴즈 제출답안 저장 */
INSERT INTO MEMBER_QUIZ_ANSWER(QUIZ_NO, MEMBER_ID, SOURCECODE, ANSWER_CHECK)
VALUES(#{quiz_No}, #{member_Id}, #{sourcecode}, #{answer_Check})

mapper.update:
/* updateQuiz - 퀴즈 수정 */
UPDATE QUIZ

    QUIZ_TITLE=#{quiz_Title},
    QUIZ_CONTENTS=#{quiz_Contents},
    QUIZ_TYPE=#{quiz_Type},
    QUIZ_LEVEL=#{quiz_Level},
    QUIZ_POINT=#{quiz_Point},
    QUIZ_PRICE=#{quiz_Price},
    QUIZ_SAMPLECODE=#{quiz_SampleCode}

WHERE QUIZ_NO = #{quiz_No}
